import React, { useState, useEffect } from "react";
import $ from "jquery";

export const UserInfo = (props) => {
  const [userId, setUserId] = useState(props.userId);
  
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");

  const getUserInfo = () => {
    $.ajax({
      cache: false,
      type: "GET",
      url: "https://localhost:44323/Users/GetUserAddress?userId=" + userId,
      success: function (data, textStatus, jqXHR) {
        var ob= JSON.parse(data);
setAddress(ob.Address);
return ob;
      },
      error: function (jqXHR, textStatus, errorThrown) {
        return "";
      },
    });
  };
const [userInfo, setUserInfo] = useState(getUserInfo);
  const guardarCambios = (e) => {
    e.preventDefault();
    var body = { address };
    $.ajax({
      cache: false,
      type: "POST",
      url: "https://localhost:44323/Users/UpdateUserAddress",
data: body,
dataType: "json",
      success: function (data, textStatus, jqXHR) {
        console.log(data);
      },
      error: function (jqXHR, textStatus, errorThrown) {
        console.log(textStatus);
      },
    });
  };
  return (
    <div className="container">
      <form className="" onSubmit={guardarCambios}>
        <div className="row">
          <div className="col-3">
            <label htmlFor="addressTxt" className="form-control-plaintext">
              Dirección
            </label>
            <label htmlFor="cityTxt" className="form-control-plaintext">
              Ciudad
            </label>
          </div>
          <div className="col-9">
            <input
              type="text"
              className="form-control"
              id="addressTxt"
              value={address}
              placeholder="Dirección"
              onChange={(e) => setAddress(e.target.value)}
              required
            />
            <input
              type="text"
              className="form-control"
              id="cityTxt"
              placeholder="Ciudad"
            />
          </div>
        </div>
        <div className="col-auto">
          <button type="submit" className="btn btn-primary mb-3">
            Guardar
          </button>
        </div>
      </form>
    </div>
  );
};
