import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckSquare, faTrash } from '@fortawesome/free-solid-svg-icons'
import $ from "jquery";

export const List = (props, {}) => {
  const [userId, setUserId] = useState(props.userId);

  const [registers, setRegisters] = useState(null);

  const getRegisterInfo = () => {
    $.ajax({
      cache: false,
      type: "GET",
      url: "https://localhost:44323/Users/GetUserList?userId=" + userId,
      success: function (data, textStatus, jqXHR) {
        var elementList = JSON.parse(data);
if(elementList.Elements==null){
elementList.Elements=[];
}
        setRegisters(elementList);
        return elementList;
      },
      error: function (jqXHR, textStatus, errorThrown) {
        return null;
      },
    });
  };

  const [list, setList] = useState(getRegisterInfo);

const deleteRegister=(registerId)=>{
if(window.confirm("¿Seguro que desea eliminar el registro?")){
$.ajax({
      cache: false,
      type: "DELETE",
      url: "https://localhost:44323/Users/DeleteElementToList?idElement=" + registerId,
      success: function (data, textStatus, jqXHR) {
        getRegisterInfo();
      },
      error: function (jqXHR, textStatus, errorThrown) {
        return null;
      },
    });
}
}

  return (
    <div className="container">
      <div className="table-responsive">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nombre</th>
              <th scope="col">Eliminar</th>
            </tr>
          </thead>
          <tbody>
            {registers !==null ? (
              registers.Elements.length>0?registers.Elements.map((registro, index) => (
                <tr key={index}>
                  <th>{index + 1}</th>
                  <td>{registro.ElementName}</td>
                  <td className="text-center">
                    <button className="btn btn-default" onClick={()=>deleteRegister(registro.Id)}>
                      <FontAwesomeIcon icon={faTrash} />
                    </button>
                  </td>
                </tr>
              )):<tr nowrap="nowrap"> <td>No hay registros en la lista</td></tr>
            ) : (
              <tr nowrap="nowrap"> <td>No hay registros en la lista</td></tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};
