import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckSquare, faTrash } from '@fortawesome/free-solid-svg-icons'
import $ from "jquery";

export const List = (props, {}) => {
  const [userId, setUserId] = useState(props.userId);

  const [registers, setRegisters] = useState(null);

  const getRegisterInfo = () => {
    $.ajax({
      cache: false,
      type: "GET",
      url: "https://localhost:44323/Users/GetUserList?userId=" + userId,
      success: function (data, textStatus, jqXHR) {
        var elementList = JSON.parse(data);
if(elementList.Elements==null){
elementList.Elements=[];
}
        setRegisters(elementList);
        return elementList;
      },
      error: function (jqXHR, textStatus, errorThrown) {
        return null;
      },
    });
  };

  const [list, setList] = useState(getRegisterInfo);

const deleteRegister=(registerId)=>{
if(window.confirm("¿Seguro que desea eliminar el registro?")){
$.ajax({
      cache: false,
      type: "DELETE",
      url: "https://localhost:44323/Users/DeleteElementToList?idElement=" + registerId,
      success: function (data, textStatus, jqXHR) {
        getRegisterInfo();
      },
      error: function (jqXHR, textStatus, errorThrown) {
        return null;
      },
    });
}
}

  return (
    <div className="container">
      <div className="table-responsive">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nombre</th>
              <th scope="col">Eliminar</th>
            </tr>
          </thead>
          <tbody>
            {registers !==null ? (
              registers.Elements.length>0?registers.Elements.map((registro, index) => (
                <tr key={index}>
                  <th>{index + 1}</th>
                  <td>{registro.ElementName}</td>
                  <td className="text-center">
                    <button className="btn btn-default" onClick={()=>deleteRegister(registro.Id)}>
                      <FontAwesomeIcon icon={faTrash} />
                    </button>
                  </td>
                </tr>
              )):<td colspan="3">No hay registros en la lista</td>
            ) : (
              <td colspan="3">No hay registros en la lista</td>
            )}
          </tbody>
        </table>
      </div>
<div className="modal fade" id="addRegister" tabindex="-1" aria-labelledby="addRegisterLabel" aria-hidden="true">
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">Agregar elemento</h5>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">
          <div className="mb-3">
            <label for="register-name" className="col-form-label">Nombre:</label>
            <input type="text" className="form-control" id="register-name"/>
          </div>
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Send message</button>
      </div>
    </div>
  </div>
</div>
    </div>
  );
};
